type GetCYs = (count: number, start: number, plus: number) => number[]

const getCYs: GetCYs = (count, start, plus) => {
  let circles = []
  for (let num = 0; num <= count - 1; num += 1) {
    circles = [...circles, start + plus * num]
  }
  return circles
}

