// React
type onClick = (event?: React.MouseEvent) => void

type onMouseEnter = (event?: React.MouseEvent) => void

type onMouseLeave = (event?: React.MouseEvent) => void

type onWheel = (event?: React.WheelEvent) => void

type onContextMenu = (event?: React.MouseEvent) => void

type onKeyPress = (event?: React.KeyboardEvent<HTMLDivElement>) => void

type onChange = (event: React.FormEvent<HTMLInputElement>) => void

// DOM
type DomMouseEvent = (event?: MouseEvent) => void
